import React,{Component} from 'react';
import {Form,Button,Checkbox,Header, Input} from 'semantic-ui-react'
import axios from 'axios';


class AddQuestion extends Component {

  constructor(props) {
    super(props);
    this.state = {
      question:"",
      questiontype:"",
      Answers: [{answer:""}],
      Surveyid:"2c022520-7f9a-11e9-9974-b543e76e34c1"
    };
     this.handleSubmit = this.handleSubmit.bind(this);
  }
   AnswersArray = [{answer:"Yes"},{answer:"No"}]


  handleChange = (e, { value }) => this.setState({ value,questiontype:value })

  showUi(){
    return this.state.Answers.map((el,i) => (
      <div key={i}>
      <label>{`${i + 1}-Answer`}</label>  
       <Input 
       placeholder="answer" 
       name="answer" 
       value={el.answer ||''}
       onChange={this.createHandleChange.bind(this, i)} 
       />

       <input 
       type='button' 
       value='Remove Answer' 
       onClick={this.removeClick.bind(this, i)}
       />

      </div>          
    ))
 }

 removeClick(i){
  let Answers = [...this.state.Answers];
  Answers.splice(i, 1);
  this.setState({ Answers });
}

questionhandleChange = (e) => {
  this.setState({
    [e.target.name]:e.target.value
  })
} 

createHandleChange(i, e) {
  const { name, value } = e.target;
  let Answers = [...this.state.Answers];
  Answers[i] = {...Answers[i], [name]: value};
  this.setState({ Answers });
}

addClick(){
  this.setState(prevState => ({ 
    Answers: [...prevState.Answers, { answer: ""}]
  }))
}

handleSubmit(event) {
  console.log(JSON.stringify(this.state));
  event.preventDefault();
  if(this.state.questiontype === "AddedAnswers"){
        let axiosConfig = {
    headers: {
      'Content-Type': 'application/json',
      'Authorization': 'Bearer ' + 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhZG1pbklkIjoiZmM1NzNiZDAtN2Y5OS0xMWU5LTk5NzQtYjU0M2U3NmUzNGMxIiwiaWF0IjoxNTU4OTQwMjU0LCJleHAiOjE1NTkwMjY2NTR9.82VHzN6ND2ZMN4mYdTHuHB6nSbD-Sy2MpkugKUqBvEk' 
    }
  };
  axios.post('http://localhost:8080/Admin/createquestion',JSON.stringify(this.state),axiosConfig)
.then(response=>{
    console.log(response)
}).catch(error=>{
    console.log(error)  
}) 
  }else{
       switch(this.state.questiontype){
            case "Yes/No":
            this.setState({
              Answers: [this.state.Answers,AnswersArray ]
            })
              alert(JSON.stringify(this.state))
            break;

            case "True/False":
              this.setState({Answers:[{answer:"True",answer:"False"}]})
              alert(JSON.stringify(this.state))
            break;  

            case "Excellent/V.good/good/Bad options":
              this.setState({Answers:[{answer:"Excellent",answer:"V.good",
              answer:"good",answer:"Bad"}]})
              alert(JSON.stringify(this.state))
            break;
       }
  }

}

 render(){  
  return(  
       <div>   
      <Header as='h2' textAlign='center'>
      New Question
    </Header> 


    <Form onSubmit={this.handleSubmit}>
    <Form.Field>
      <label>Question</label>
      <input 
      placeholder='new question'
        name='question'
        defaultValue={this.state.question}
       onChange={this.questionhandleChange}
      />
    </Form.Field>   


    <label>Question Type</label>
    <Form.Field>
        </Form.Field>
        <Form.Field>
          <Checkbox
            radio
            label='Yes/No'
            name='checkboxRadioGroup'
            value='Yes/No'
            checked={this.state.value === 'Yes/No'}
            onChange={this.handleChange}
          />
        </Form.Field>

        <Form.Field>
          <Checkbox
            radio
            label='True/False'
            name='checkboxRadioGroup'
            value='True/False'
            checked={this.state.value === 'True/False'}
            onChange={this.handleChange}
          />
        </Form.Field>

        <Form.Field>
          <Checkbox
            radio
            label='Excellent/V.good/good/Bad options'
            name='checkboxRadioGroup'
            value='Excellent/V.good/good/Bad options'
            checked={this.state.value === 'Excellent/V.good/good/Bad options'}
            onChange={this.handleChange}
          />
        </Form.Field>



        <Form.Field>
          <Checkbox
            radio
            label='Add Answers'
            name='checkboxRadioGroup'
            value='AddedAnswers'
            checked={this.state.value === 'AddedAnswers'}
            onChange={this.handleChange}
          />
        </Form.Field>


        { 
          this.state.questiontype && this.state.questiontype === 'AddedAnswers'?
        <Form.Field>
         <div>
        {this.showUi()}        
       <input type='button' value='Add Answer' onClick={this.addClick.bind(this)}/>
        </div> 
        </Form.Field>  
        :null
        }
        <br/>
    <Button fluid color='red'>Create New Question</Button>

  </Form>
            </div>
          );

      }


}



export default AddQuestion
