import React,{Component} from 'react';
import {Form,Button,Checkbox,Header} from 'semantic-ui-react'
import axios from 'axios';


class AddQuestion extends Component {

  constructor(props) {
    super(props);
    this.state = {
      attrValue: false
    };
    this._handleInputChange = this._handleInputChange.bind(this);
  }
  _handleInputChange(e) {
      const element = e.target;
      this.setState({attrValue: element.checked});
  }

      render(){     
        const content = this.state.checked 
        ? <div> Content </div>
        : null;

          return(  
            <div>   
      <Header as='h2' textAlign='center'>
      New Question
    </Header>   
         <Form>
    <Form.Field>
      <label>Question</label>
      <input placeholder='new question'/>
    </Form.Field>   


    <label>Question Type</label>
    <br/>
    <br/>

       <Form.Field>
          <Checkbox
            radio
            label='Yes/No'
            name='checkboxRadioGroup'
          />
        </Form.Field>

       <Form.Field>
          <Checkbox
            radio
            label='True/False'
            name='checkboxRadioGroup'
          />
        </Form.Field>


        <Form.Field>
          <Checkbox
            radio
            label='Multiple(Excellent,V.good,Good,Bad,V.Bad)'
          />
        </Form.Field>


        <Form.Field>
          <Checkbox
            radio
            label='Other'
            checked={this.state.attrValue}
            onChange={this._handleInputChange}
            name="attrValue"
          />
        </Form.Field>

        <br/>
        <br/>
        {`State of checkbox: ${this.state.attrValue}`}

    <Button fluid color='red'>Create New Question</Button>

  </Form>


            </div>
          );

      }


}



export default AddQuestion
