import React,{Component} from 'react';
import {Form,Button,TextArea,Header} from 'semantic-ui-react'
import axios from 'axios';


class AddSurvey extends Component {

     state ={
        title:'',
        description:''
     }


     handleChange = (e) => {
          this.setState({
            [e.target.name]:e.target.value
          })
      }   


    createSurveyHandler = () =>{
        let axiosConfig = {
            headers: {
              'Content-Type': 'application/json',
              'Authorization': 'Bearer ' + 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhZG1pbklkIjoiMjRjM2RkODAtN2EyNy0xMWU5LThkZGYtYmJkMzIxM2Q2NGRhIiwiaWF0IjoxNTU4MjY0NDI2LCJleHAiOjE1NTgzNTA4MjZ9.I_W3K-IsdL5YAShsntlY4e7-ZievuSxH_SMniruVqvQ' 
            }
          };

        axios.post('http://localhost:8080/Admin/createsurvey',this.state,axiosConfig)
        .then(Response=>{
            console.log(Response)
        }).catch(error=>{
            console.log(error)
        })  
    }  

      render(){
          return(
            <div>   
      <Header as='h2' textAlign='center'>
      New Survey
    </Header>
         <Form>
    <Form.Field>
      <label>Survey Title</label>
      <input placeholder='survey title' 
         name='title'
         defaultValue={this.state.title}
        onChange={this.handleChange}
      />
    </Form.Field>

    <Form.Field>
      <label>Survey Description</label>
      <TextArea placeholder='survey description'
        name='description'
        defaultValue={this.state.description}
       onChange={this.handleChange}
      />
    </Form.Field>

    <Button fluid color='pink' onClick={this.createSurveyHandler}>Create New Survey</Button>

  </Form>
            </div>
          );

      }


}



export default AddSurvey
