import React,{Component} from 'react';
import {Form,Button,Checkbox,Header,Input} from 'semantic-ui-react'
import axios from 'axios';


class AddQuestion extends Component {

  constructor(props) {
    super(props);
    this.state = {}
    this.state = {
    	Answers: [{answer:""}]
    };

  }


  handleChange = (e, { value }) => this.setState({ value })

  showUi(){
    return this.state.Answers.map((el,i) => (
      <div key={i}>
       <input 
       placeholder="answer" 
       name="answer" 
       value={el.answer ||''}
       onChange={this.handleChange.bind(this, i)} 
       />
      </div>          
    ))
 }

 handleChange(i, e) {
  const { name, value } = e.target;
  let Answers = [...this.state.Answers];
  Answers[i] = {...Answers[i], [name]: value};
  this.setState({ Answers });
}

 render(){  
  return(  
       <div>   
      <Header as='h2' textAlign='center'>
      New Question
    </Header> 


    <Form>
    <Form.Field>
      <label>Question</label>
      <input placeholder='new question'/>
    </Form.Field>   


    <label>Question Type</label>
    <Form.Field>
        </Form.Field>
        <Form.Field>
          <Checkbox
            radio
            label='Yes/No'
            name='checkboxRadioGroup'
            value='Yes/No'
            checked={this.state.value === 'Yes/No'}
            onChange={this.handleChange}
          />
        </Form.Field>

        <Form.Field>
          <Checkbox
            radio
            label='True/False'
            name='checkboxRadioGroup'
            value='True/False'
            checked={this.state.value === 'True/False'}
            onChange={this.handleChange}
          />
        </Form.Field>

        <Form.Field>
          <Checkbox
            radio
            label='Excellent/V.good/good/Bad options'
            name='checkboxRadioGroup'
            value='Excellent/V.good/good/Bad options'
            checked={this.state.value === 'Excellent/V.good/good/Bad options'}
            onChange={this.handleChange}
          />
        </Form.Field>


        <Form.Field>
          <Checkbox
            radio
            label='Create Answers'
            name='checkboxRadioGroup'
            value='Create Answers'
            checked={this.state.value === 'Create Answers'}
            onChange={this.handleChange}
          />
        </Form.Field>
          {this.state.value && this.state.value === "Create Answers"?
            <div>
              <label>New Answers?</label>
              {this.showUi}
            </div>
          :null  
          }
          <br/>   
          <br/>

          <Form.Field>
          <Button 
          onClick={this.addClick.bind(this)}>
          Add More
          </Button>
          </Form.Field>

    <Button fluid color='red'>Create New Question</Button>

  </Form>
            </div>
          );

      }


}



export default AddQuestion
