import React,{Component} from 'react';
import {Form,Button,Checkbox,Header} from 'semantic-ui-react'
import axios from 'axios';


class AddQuestion extends Component {

  state = {
    isMJ: false,
    isJB: false,
    isDrake: false
  };

  toggleChangeMJ = () => {
    this.setState(prevState => ({
      isMJ: !prevState.isMJ,
    }));
  }

  toggleChangeJB = () => {
    this.setState(prevState => ({
      isJB: !prevState.isJB,
    }));
  }

  toggleChangeDrake = () => {
    this.setState(prevState => ({
      isDrake: !prevState.isDrake,
    }));
  }

  onSubmit = (e) => {
    e.preventDefault();
    console.log(this.state);
  }

      render(){  

        const content = this.state.checked 
        ? <div> Content </div>
        : null;


          return(  
            <div>   
      <Header as='h2' textAlign='center'>
      New Question
    </Header>   
         <Form>
    <Form.Field>
      <label>Question</label>
      <input placeholder='new question'/>
    </Form.Field>   


    <label>Question Type</label>
    <br/>
    <br/>

       <Form.Field>
          <Checkbox
            radio
            label='Yes/No'
            name='checkboxRadioGroup'
          />
        </Form.Field>

       <Form.Field>
          <Checkbox
            radio
            label='True/False'
            name='checkboxRadioGroup'
          />
        </Form.Field>


        <Form.Field>
          <Checkbox
            radio
            label='Multiple(Excellent,V.good,Good,Bad,V.Bad)'
          />
        </Form.Field>


        <Form.Field>
          <Checkbox
            radio
            label='Other'
            checked={this.state.isDrake}
                onChange={this.toggleChangeDrake}
          />
        </Form.Field>
        { content }
    <Button fluid color='red'>Create New Question</Button>

  </Form>
            </div>
          );

      }


}



export default AddQuestion
